mapper.select:
select 
from employees
where emp_num = #{empNum, jdbcType=VARCHAR}

mapper.update:
update employees
emp_Name = #{empName}, emp_ADDR = #{empAddr}, emp_Addr_Detail = #{empAddrDetail}, emp_Post = #{empPost}, emp_jumin = #{empJumin}, emp_Phone = #{empPhone}, emp_Email = #{empEmail}
where emp_num = #{empNum, jdbcType=VARCHAR}
