mapper.select:
select 
from members
where member_num = #{memberNum, jdbcType=VARCHAR}

mapper.update:
update members
member_Name = #{memberName}, member_ADDR = #{memberAddr}, member_Addr_Detail = #{memberAddrDetail}, member_Post = #{memberPost}, gender = #{gender}, member_Phone = #{memberPhone}, member_Email = #{memberEmail}
where MEMBER_NUM = #{memberNum, jdbcType=VARCHAR}

mapper.select:
select * from (select ROW_NUMBER() over (order by member_num ) as rn , from members
MEMBER_NAME like '%'||#{searchWord}||'%' or MEMBER_ID like '%'||#{searchWord}||'%' or MEMBER_NUM like '%'||#{searchWord}||'%'
) where rn BETWEEN #{startRow} and #{endRow}
